# test_api.py
import requests

API_URL = "http://127.0.0.1:8000/analyze-post"

print('------------------------------------------- Bad test')
data = {
    "text": [
        "Buy right now your Advent Calendar!"
    ],
    "image_url": "data:image/webp;base64,"
}

print("Sending request to API...")
response = requests.post(API_URL, json=data)
print(f"Status code: {response.status_code}")
print("Response:")
print(response.json())

print('------------------------------------------- Good test')
